sub _get_config_file {
	my $self = shift;
	my $conf_path="priv";
	if ( $0 =~ /res\/cm/ ) {
		$conf_path="../../priv";
	} elsif ( $0 =~ /res|login/ ) {
		$conf_path="../priv";
#API
 	} elsif ( $0 =~ /api\/intapi/ ) {
 		$conf_path="../priv";
 	} elsif ( $0 =~ /intapi/ ) {
 		$conf_path="./priv";
 	} elsif ( $0 =~ /api/ ) {
 		$conf_path="../priv";
	}
	my $config_file="$conf_path/ip_config";
	return $config_file;
}

