$number_free_adds = Math::BigInt->new("$number_free_adds");
$number_free_adds++;
if ( $number_free_adds == 0 ) {
    return;
}
my $first_free_range_add_int=$args{first_free_range_add_int} || "";
my $free_message=$lang_vars{direcciones_libres_message};
