    foreach $listeneintrag (@datenliste) {
        if ( $listeneintrag !~ /.{1,10}=.{0,1000}/ ) { next; }
        $listeneintrag =~ /=(\+){1}/;
        my $first_plus = $1 || "";
        my $i = "0";
        my @plus;
        while ( 1 == 1 ) {
            if ( $listeneintrag =~ /\+\+/ && ( $listeneintrag =~ /^hostname=/ || $listeneintrag =~ /^red_search=/ ) ) {
                $listeneintrag =~ s/(\+\+[^+]*)//;
                $plus[$i] = $1;
                $i++;
            } else {
                last;
            }
        }

        $listeneintrag =~ s/\+/ /go;
        foreach ( @plus ) {
            $_ =~ s/^\+//;
            $listeneintrag = $listeneintrag . " $_"; 
        }
        $listeneintrag =~ s/= /=$first_plus/ if $first_plus;
        
        $listeneintrag =~ /^(.+?)=(.*)$/s;
        $name=$1;
        if ( defined($2) ) { 
