    while ( $ip_ref = $sth->fetchrow_hashref ) {
    my $red = $ip_ref->{red};
    my $BM = $ip_ref->{BM};
    my $red_num = $ip_ref->{red_num};
    my $cat_net = $ip_ref->{cat};
    my $descr = $ip_ref->{descr};
    my $cat = $ip_ref->{cat};
    my $loc = $ip_ref->{loc};
    my $comentario = $ip_ref->{comentario};
