sub exec_ip_open {
my ($self) = @_;
my $error="";
my %ips=();
my $if="";
my $if_index="";
my $command="";
if ( -e "/bin/ip" ) {
    $command="/bin/ip";
} elsif ( -e "/sbin/ip" ) {
    $command="/sbin/ip";
}
if ( ! $command ) {
    $ips{error}="COMMAND 'ip' NOT FOUND";
    return %ips
}
open(IP,"$command addr |") or $error=$!;
while ( <IP> ) {
    if ( $_ =~/^\d+: / ) {
        $_ =~ /^(\d+): (\w+):/;
        $if_index=$1;
        $if=$2;
    }
    
    next if $_ !~ /inet/;
    next if $_ =~ /inet6 ::1\/128/;
    next if $_ =~ /127.0.0.1/;
    $_=~ /inet.? (.+\/\d{1,3}).*/;
    my $ip=$1;
    $ip =~ s/\/\d{1,3}//;
    if ( $ip =~ /:/ ) {
        $ip=$ip . " " . $if;
    }
    $ip =~ s/\/d{1,3}//;
    $ips{$ip}++;
}
$ips{error}=$error;

return %ips;
}

sub ping_system {
my ($self, $ip, $timeout) = @_;

my @args;
$timeout = 2 if ! $timeout || $timeout !~ /^\d{1,3}$/;

$ENV{PATH} = "/bin";
my $devnull = "/dev/null";
my $command = "/bin/ping -c 2 -W $timeout -w 6 " . $ip . " 1>$devnull 2>&1";
my $exit_status = system($command) >> 8;

# O = OK
return $exit_status;
}

