    $job_id=~s/,/|/g;	
}
my @files=();
opendir (DIR, $dir) or croak $self->print_error("$client_id","Can not open log file directory \"$dir\": $!");
my $j=0;
while (my $file = readdir(DIR)) {
    next if $file eq "." || $file eq "..";
