sub encryptString {
my ( $self,$master_key,$user_password ) = @_;

use Crypt::CBC;
use MIME::Base64;

my $cipher = Crypt::CBC->new(
    -key        => $user_password,
    -cipher     => 'Blowfish',
    -padding  => 'space',
    -add_header => 1
);

my $enc = $cipher->encrypt($master_key);
my $enc_mime = encode_base64($enc);
return $enc_mime; 
}


sub decryptString {
my ( $self,$master_key_enc_mime,$user_password ) = @_;

use Crypt::CBC;
use MIME::Base64;

my $master_key_enc = decode_base64($master_key_enc_mime);
my $cipher = Crypt::CBC->new(
    -key        => $user_password,
    -cipher     => 'Blowfish',
    -padding  => 'space',
    -add_header => 1
);

my $dec = $cipher->decrypt($master_key_enc);
# untaint $dec
$dec =~ /^(.*)$/;
$dec=$1;

return $dec; 
}

