my %colors;
open(FILE, "<$file") or debug("Can not open file: $!\n");
while (<FILE>) {
    $_ =~ s/\s+//g;
    $_ =~ /^(.+):(.*)/;
    my $color = $1;
    my $vlans = $2;
    my @vlans = split(",", $vlans);
    foreach ( @vlans ) {
        if ( $_ =~ /^\d+$/ ) {
            $colors{$_} = $color; 
        } elsif ( $_ =~ /-/ ) {
            $_ =~ /^(\d+)-(\d+)$/;
            my $start = $1;
            my $end = $2;
            if ( $end > $start ) {
                for ( my $i = $start; $i <= $end; $i++) {
                    $colors{$i} = $color;
                }
            }
        }
    }
}
