sub get_wmask_from_bm {
my ( $self, $bm ) = @_;

my %bm = $self->get_bm_to_netmask();
my $mask = $bm{$bm};

my $mask_wild_packed = pack 'C4', split /\./, $mask;
my $mask_packed = ~$mask_wild_packed;
my $wmask = join '.', unpack 'C4', $mask_packed;

return $wmask;
}


sub compare_array {
my ($self, $A, $B) = @_;

# build lookup table
my %seen = map { $_ => 1 } @$B;

# find those in A that aren't in B
my @aonly;
foreach my $item (@$A) {
  push @aonly,$item unless ($seen{$item});
}

return \@aonly;
}  


sub compare_hash {
my ($self, $A, $B) = @_;

my @aonly = ();
my @bfound = ();

foreach my $key_a (keys %$A) {
    next if ! $key_a;
    my $value_a = $A->{$key_a};
    if ( $key_a =~ /%%/ ) {
        $key_a =~ /^(.+)%%(.+)$/;
        my $acl1 = $1;
        my $acl2 = $2;
        if ( ! exists $B->{$acl1} && ! exists $B->{$acl2} && ! exists $B->{$key_a} ) {
            push (@aonly, $key_a);
        } else {
            push (@bfound, $value_a);
        }
    } else {
        if ( ! exists $B->{$key_a} ) {
            push (@aonly, $key_a);
        }
    }
}

return \@aonly;
}

sub compare_hash_array {
my ($self, $A, $B) = @_;

my @aonly = ();
my @bfound = ();

foreach my $key_a (keys %$A) {
    next if ! $key_a;
    my $value_a = $A->{$key_a};
    foreach my $con ( @$value_a ) {
        if ( $key_a =~ /%%/ ) {
            $key_a =~ /^(.+)%%(.+)$/;
            my $acl1 = $1;
            my $acl2 = $2;
            if ( ! exists $B->{$acl1} && ! exists $B->{$acl2} && ! exists $B->{$key_a} ) {
                push (@aonly, $key_a);
            } else {
                push (@bfound, $value_a);
            }
        } else {
            if ( ! exists $B->{$key_a} ) {
                push (@aonly, $key_a);
            }
        }
    }
}

return \@aonly;
}


