    $crontab_cmd =~ /^(.*)$/;
    my $crontab = $1;

    my $echo_cmd = "/bin/echo";
    $echo_cmd =~ /^(.*)$/;
    my $echo = $1;

    $entry =~ /^(.*)$/;
    $entry = $1;

	my $command = $crontab . ' -l';
	my $output = `$command 2>&1`;

	if ( $output =~ /crontab/ ) {
        $command = $echo . ' \'' . $entry . '\' | ' . $crontab . ' -';
    } else {
        $command = '(' . $crontab . ' -l && ' . $echo . ' \'' . $entry . '\') | ' . $crontab . ' -';
    }
    debug("Executing: $command");
	$output = `$command 2>&1`;
	if ( $output ) {
		return $output;
	}
}

