       $columns .= ", " . $col->[0];
    }
    $columns =~ s/^, //;

    my $dbh = $self->_mysql_connection();

    my $id_expr = "";
    if ( $id ) {
        my $qid = $dbh->quote( $id );
        $id_expr = "AND id=$qid";
    }

    my $qclient_id = $dbh->quote( $client_id );

